const mockWishlistData = [
  {
    title: "Item A",
    tags: ["tagA", "tagB"],
    thumbnail:
      "",
  },
  {
    title: "Item B",
    tags: ["tagC", "tagD"],
    thumbnail:
      "",
  },
  {
    title: "Item C",
    tags: ["tagE", "tagF"],
    thumbnail:
      "",
  },
];

function Lists() {
  const [wardrobeItems, setWardrobeItems] = useState([]);
  const [wishlistItems, setWishlistItems] = useState([]);

  useEffect(() => {
    setWardrobeItems(mockWardrobeData);
    setWishlistItems(mockWishlistData);
  }, []);

  const [scrollPosition, setScrollPosition] = useState(0);
  const handleScroll = () => {
    const position = window.pageYOffset;
    setScrollPosition(position);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll, { passive: true });

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <ChakraProvider theme={theme}>
      <Box
        minHeight="100vh"
        height="100vh"
        bg="#ffffff"
        color="#213547"
        p="4"
        className="w-screen"
        position="static"
      >
        <Tabs variant="unstyled" isFitted>
          <TabList mb="1em">
            <Tab _active={{ bg: "gray.50" }}>Wardrobe</Tab>
            <Tab _active={{ bg: "gray.50" }}>Wishlist</Tab>
          </TabList>
          <TabIndicator mt="-10px" height="2px" bg="black" borderRadius="1px" />
          <TabPanels>
            <TabPanel>
              <Box h="full" overflowY="auto">
                {wardrobeItems.map((item, index) => (
                  <ItemCard
                    key={index}
                    title={item.title}
                    description={item.tags.join(", ")}
                    thumbnail={item.thumbnail}
                  />
                ))}
              </Box>
            </TabPanel>
            <TabPanel>
              <Box overflowY="auto">
                {wishlistItems.map((item, index) => (
                  <ItemCard
                    key={index}
                    title={item.title}
                    description={item.tags.join(", ")}
                    thumbnail={item.thumbnail}
                  />
                ))}
              </Box>
            </TabPanel>
          </TabPanels>
        </Tabs>
      </Box>
      {scrollPosition > 100 && <ScrollToTopButton />}
    </ChakraProvider>
  );
}

function ItemCard({ title, description, thumbnail }) {
  return (
    <Box
      bg="#f9f9f9"
      p={4}
      borderRadius="8px"
      mb={4}
      border="1px solid #e2e2e2"
    >
      <Box display="flex" alignItems="center" justifyContent="start" mb={2}>
        <img
          src={`data:image/png;base64,${thumbnail}`}
          alt={title}
          className="w-12 h-12 rounded-md mr-3"
        />
        <Box>
          <Box fontWeight="500" className="text-left">
            {title}
          </Box>
          <Box fontSize="sm" color="gray.500">
            {description}
          </Box>
        </Box>
      </Box>
    </Box>
  );
}

function ScrollToTopButton() {
  const handleClick = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <Button
      onClick={handleClick}
      position="fixed"
      bottom="4"
      right="4"
      colorScheme=""
      variant="outline"
    >
      Scroll to Top
    </Button>
  );
}

export default Lists;
